if(window.location.search.indexOf("full")!==-1){document.body.className+="full"}document.addEventListener("DOMContentLoaded",function(){const goToTopEl=document.getElementById("goToTop");const content=document.getElementById("content");window.addEventListener("scroll",function(el,e){var top=typeof(window.scrollY==="undefined")?window.pageYOffset:window.scrollY;goToTopEl.style.display=top<5?"none":"block"});content.addEventListener("scroll",function(el,e){goToTopEl.style.display=content.scrollTop<5?"none":"block"});goToTopEl.addEventListener("click",function(event){content.scrollTo(0,0);window.scrollTo(0,0)})});